const Field = ({ setInput, value, fieldTitle, inputType, placeholder }) => {
    return (
        <div>
            <h2>{fieldTitle}</h2>
            <input
                value={value}
                type={inputType}
                placeholder={placeholder}
                onChange={(event) => {
                    setName(event.target.value);
                }}
            />
        </div>
    );
};

export default Field;
